var zoomLevelPercentage=100;function zoomIn(){zoomLevelPercentage+=25,applyZoom()}function zoomOut(){25<zoomLevelPercentage?zoomLevelPercentage-=25:5<zoomLevelPercentage?zoomLevelPercentage-=5:zoomLevelPercentage=5,applyZoom()}function resetZoom(){zoomLevelPercentage=100,applyZoom()}function applyZoom(){var e=document.querySelector(".chart"),o=e.scrollTop/e.scrollHeight,l=e.scrollLeft/e.scrollWidth;document.querySelectorAll(".chart > *").forEach(e=>{e.style.zoom=zoomLevelPercentage+"%"}),e.scrollTop=o*e.scrollHeight,e.scrollLeft=l*e.scrollWidth}